import React from 'react'
import { Link } from 'react-router-dom';

class LandingPage extends React.Component {
    constructor(props) {
        super(props)
    }

    componentDidMount() {
        const date = new Date()
        this.props.fetchAllBlocks(date.getTime())
        // this.props.fetch
    }

    blockTolist() {
        debugger
        return Object.values(this.props.blocks).map(block => {
            const date = new Date(block.time * 1000).toUTCString()
            return (
                <div>
                    <div><Link to={`/block/${block.hash}`}>{block.height}</Link></div>
                    <div><Link to={`/block/${block.hash}`}>{block.hash}</Link></div>
                    <div>{date}</div>
                </div>
            )
        })
    }

    render() {
        if (this.props.blocks === {}) return null;

        return (
            <div className="landing-page">
                <div className="=side-bar">

                </div>

                <div className="block-list">
                    <h2>Latest Blocks</h2>
                    {this.blockTolist()}
                </div>
            </div>
        )
    }
}

export default LandingPage
