import React from 'react'
import { Link } from 'react-router-dom'

class TransactionItem extends React.Component {
    constructor(props) {
        super(props)
    }



    render() {
        // key = { i }
        // hash = { hash }
        // input = { input }
        // output = { output }
        // time = { time }
        // currVal = { currVal }
        debugger
        const { hash, inputs, outputs, time, currVal } = this.props
        const inputAddr = inputs.map((input, i) => {
            return <li key={i}><Link to={`/address/${input.addr}`}>{input.addr}</Link></li>
        })
        const outputAddr = outputs.map((output, i) => {
            return <li key={i}><Link to={`/address/${output.addr}`}>{output.addr}</Link></li>
        })

        return (
            <div className="single-tx">
                <div className="tx-hash">
                    {hash}
                </div>
                <div>
                    <div className="from-addr-col">
                        <ul>
                            {inputAddr}
                        </ul>
                    </div>
                    <div>
                        {/* <i className="" */}
                    </div>
                    <div>
                        <ul>
                            {outputAddr}
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}

export default TransactionItem
