import React from 'react'
import { Link } from 'react-router-dom'
import TransactionList from '../transaction/transaction_list'

class Address extends React.Component {
    constructor(props) {
        super(props)
    }

    componentDidMount() {
        this.props.fetchAddress(this.props.match.params.address)
    }

    componentWillReceiveProps(nextProps) {
        if (this.props.match.params.address !== nextProps.match.params.address) {
            this.props.fetchBlock(nextProps.match.params.address);
        }
    }

    convertToBTC(value) {
        return value / 100000000
    }

    render() {
        const { address } = this.props;
        if (Object.entries(address).length === 0) return null;
        debugger
        return (
            <div>
                <div className="single-address">
                    <h3 className="address-pg-height">Bitcoin Address</h3>
                    <div className="address-info">
                        <div className="address-row">
                            <p className="address-hash"><strong>Summary</strong></p>
                        </div>
                        <div className="address-row">
                            <p>Address</p>
                            <p className="address-hash">{address.n_tx}</p>
                        </div>
                        <div className="address-row">
                            <p>Hash 160</p>
                            <p className="address-hash">{address.hash160}</p>
                        </div>
                        <div className="address-row">
                            <p className="address-hash"><strong>Transactions</strong></p>
                        </div>
                        <div className="address-row">
                            <p>No. Transactions</p>
                            <p className="address-hash">{address.n_tx}</p>
                        </div>
                        <div className="address-row">
                            <p>Total Received</p>
                            <p className="address-hash">{address.total_received}</p>
                        </div>
                        <div className="address-row">
                            <p>Final Balance</p>
                            <p className="address-hash">{address.final_balance}</p>
                        </div>
                    </div>
                    <TransactionList txsArr={address.txs} />
                </div>
            </div>
        )
    }
}

export default Address
