import React from 'react'
import { Link } from 'react-router-dom';
import style from '../../../App.css'

class LandingPage extends React.Component {
    constructor(props) {
        super(props)
    }

    componentDidMount() {
        const date = new Date()
        this.props.fetchAllBlocks(date.getTime())
        // this.props.fetch
    }

    blockTolist() {
        // debugger
        return Object.values(this.props.blocks).map(block => {
            const date = new Date(block.time * 1000).toUTCString()
            const short_block_hash = block.hash.substring(0,23) + "..."
            return (
                <div>
                    <div className={style.block}>
}                        <div className={style.blockheight}><Link to={`/block/${block.hash}`}>{block.height}</Link></div>
                        <div className={style.blockhash}><Link to={`/block/${block.hash}`}>{short_block_hash}</Link></div>
                        <div className={style.blockdate}>{date}</div>
                    </div>
                </div>
            )
        })
    }

    render() {
        if (this.props.blocks === {}) return null;

        return (
            <div className={style.landingpage}>
                <div className={style.sidebar}>

                </div>

                <div className={style.blocklist}>
                    <h2>Latest Blocks</h2>
                    <div className={style.blockcolumns}>
                        <p className={style.height}>Height</p>
                        <p className={style.hash}>Hash</p>
                        <p className={style.time}>Time</p>
                    </div>
                    {this.blockTolist()}
                </div>
            </div>
        )
    }
}

export default LandingPage
