import React from 'react'
import { Link } from 'react-router-dom'

class TransactionItem extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        const { hash, inputs, outputs, time, currVal } = this.props
        let inputAddr;

        if (inputs.length === 0) {
            inputAddr = <li>No Inputs (Newly Generated Coin)</li>
        } else {
            inputAddr = inputs.map((input, i) => {
                return <li key={i}><Link to={`/address/${input.address}`}>{input.address.slice(0,30) + "..."}</Link></li>
            }) 
        }
        const outputAddr = outputs.map((output, i) => {
            if (output.address) {
                return <li key={i}><Link to={`/address/${output.address}`}>{output.address.slice(0, 30) + "..."}</Link></li>
            } else {
                return <li key={i}>Unable to decode output address</li>
            }
        })

        return (
            <div className="single-tx">
                <div className="tx-hash">
                    <Link to={`/address/${hash}`}>{hash}</Link>

                    <div className="tx-time">
                        {time}
                    </div>
                </div>
                <div className="tx-info">
                    <div className="in-addr-col">
                        <ul>{inputAddr}</ul>
                    </div>

                    <div className="tx-arrow">
                        <i className="fas fa-arrow-right"></i>
                    </div>

                    <div className="out-addr-col">
                        <ul>{outputAddr}</ul>
                    </div>

                    <div className="tx-value">
                        {currVal.toString().slice(0,12) + " BTC"}
                    </div>
                </div>
            </div>
        )
    }
}

export default TransactionItem
