import React from 'react';
import TransactionItem from './transaction_item';

class TransactionList extends React.Component {
    constructor(props) {
        super(props)
    }

    convertToTime(unix) {
        const date = new Date(unix * 1000)
        return date.toLocaleDateString('default', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })
    }

    convertToBTC(value) {
        return value / 100000000
    }

    inputArr(inputs) {
        let modifiedInputs = []
        inputs.forEach(input => {
            debugger
            const prev = input.prev_out
            const address = prev.addr
            const value = this.convertToBTC(prev.value)
            var newInput = {
                'address': address,
                'value': value,
            }
            modifiedInputs.push(newInput)
        })
        return modifiedInputs;
    }

    outputArr(outputs) {
        let modifiedOutputs = []
        outputs.forEach(out => {
            debugger
            const address = out.addr
            const value = this.convertToBTC(out.value)
            const newOutput = {
                'address': address,
                'value': value,
            }
            modifiedOutputs.push(newOutput)
        })
        return modifiedOutputs
    }

    diffPerTx(input, output) {
        let currVal = 0

        input.forEach(input => {
            currVal -= parseFloat(input.value)
        })

        output.forEach(output => {
            currVal += parseFloat(output.value)
        })

        return currVal
    }

    createTxList() {
        const allTx = this.props.txsArr.map((tx, i) => {
            debugger
            const hash = tx.hash
            const input = this.inputArr(tx.inputs) 
            const output = this.outputArr(tx.out)
            const time = this.convertToTime(tx.time)
            const currVal = this.diffPerTx(input, output)
            return (
                <TransactionItem
                    key={i}
                    hash={hash}
                    input={input}
                    output={output}
                    time={time}
                    currVal={currVal}
                />
            )
        })
        return allTx
    }

    render() {
        if (this.props.txsArr.length === undefined) return null;
        const TxList = this.createTxList()

        return (
            <div className="transaction-list">
                <h3>Transactions</h3>
                {TxList}
            </div>
        )
    }

}

export default TransactionList
