import React from 'react'
import TransactionItem from './transaction_item'

class Transaction extends React.Component {
    constructor(props) {
        super(props)
    }

    componentDidMount() {
        debugger
        this.props.fetchTx(this.props.match.params.tx)
    }

    convertToTime(unix) {
        const date = new Date(unix * 1000)
        return date.toLocaleDateString('default', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })
    }

    inputArr(inputs) {
        let modifiedInputs = []
        inputs.forEach(input => {
            if (input.prev_out !== undefined) {
                const prev = input.prev_out
                const address = prev.addr
                const value = this.convertToBTC(prev.value)
                var newInput = {
                    'address': address,
                    'value': value,
                }
                modifiedInputs.push(newInput)
            }
        })
        return modifiedInputs;
    }

    outputArr(outputs) {
        let modifiedOutputs = []
        outputs.forEach(out => {
            const address = out.addr
            const value = this.convertToBTC(out.value)
            const newOutput = {
                'address': address,
                'value': value,
            }
            modifiedOutputs.push(newOutput)
        })
        return modifiedOutputs
    }

    totalInput(inputs) {
        let prevVal = 0
        inputs.forEach(input => {
            prevVal += parseFloat(input.val)
        })
        return prevVal;
    }

    diffPerTx(outputs) {
        let currVal = 0
        outputs.forEach(output => {
            currVal += parseFloat(output.value)
        })
        return currVal
    }

    createTxItem () {
        const { tx } = this.props
        const hash = tx.hash
        const inputs = this.inputArr(tx.inputs)
        const outputs = this.outputArr(tx.out)
        const currVal = this.diffPerTx(outputs)
        return (
            <TransactionItem 
                hash={hash}
                inputs={inputs}
                outputs={outputs}
                currVal={currVal}
            />
        )
    }

    render() {
        const { tx } = this.props
        if (tx === undefined) return null;

        const txItem = this.createTxItem()
        const inputs = this.inputArr(tx.inputs)
        const outputs = this.outputArr(tx.out)
        const currVal = this.diffPerTx(outputs)
        const prevVal = this.totalInput(inputs)

        return (
            <div className="tx-page">
                <h3 className="tx-title">Transactions</h3>
                <div className="tx-desc">
                    <div className="tx-desc-1">
                        <div className="tx-summary-row">
                            <p className="tx-summary">Summary</p>
                        </div>
                        <div className="row">
                            <p>Weight</p>
                            <p>{tx.weight}</p>
                        </div>
                        <div className="row">
                            <p>Size</p>
                            <p>{tx.size}</p>
                        </div>
                        <div className="row">
                            <p>Received Time</p>
                            <p>{tx.size}</p>
                        </div>
                        <div className="row">
                            <p>Included In Block</p>
                            <p>{tx.block_height}</p>
                        </div>
                    </div>
                    <div className="tx-desc-2">
                        <div className="tx-summary-row">
                            <p className="tx-summary">Inputs and Outputs</p>
                        </div>
                        <div className="row">
                            <p>Total Input</p>
                            <p>{prevVal}</p>
                        </div>
                        <div className="row">
                            <p>Total Output</p>
                            <p>{currVal}</p>
                        </div>
                    </div>
                </div>
                {txItem}
            </div>
        )
    }
}

export default Transaction
