import React from 'react'
import { Link } from 'react-router-dom'
import TransactionList from '../transaction/transaction_list'

class Address extends React.Component {
    constructor(props) {
        super(props)
    }

    componentDidMount() {
        this.props.fetchAddress(this.props.match.params.address)
    }

    componentWillReceiveProps(nextProps) {
        if (this.props.match.params.address !== nextProps.match.params.address) {
            this.props.fetchBlock(nextProps.match.params.address);
        }
    }

    convertToTime(unix) {
        const date = new Date(unix * 1000)
        return date.toLocaleDateString('default', { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric' })
    }

    render() {
        const { address } = this.props;
        if (Object.entries(address).length === 0) return null;
        debugger
        return (
            <div>
                <div className="single-block">
                    <h3 className="block-pg-height">Bitcoin Address</h3>
                    <div className="block-info">
                        <div className="block-info-1">
                            <div className="block-summary-row">
                                <p className="block-summary">Summary</p>
                            </div>
                            <div className="row">
                                <p>Address</p>
                                <p>{address.n_tx}</p>
                            </div>
                            <div className="row">
                                <p>Hash 160</p>
                                <p>{address.hash160}</p>
                            </div>
                        </div>

                        <div className="block-info-2">
                            <div className="block-summary-row">
                                <p className="block-summary">Transactions</p>
                            </div>
                            <div className="row">
                                <p>No. Transactions</p>
                                <p className="block-hash">{address.n_tx}</p>
                            </div>
                            <div className="row">
                                <p>Total Received</p>
                                <p className="block-hash">{address.total_received}</p>
                            </div>
                            <div className="row">
                                <p>Final Balance</p>
                                <p className="block-hash">{address.final_balance}</p>
                            </div>
                        </div>
                    </div>
                    <TransactionList txsArr={address.txs} />
                </div>
            </div>
        )
    }
}

export default Address
